import { useEffect, useState } from "react";

function Home() {

    const [metroLineData, setMetroLineData] = useState([])

    const apiKey = process.env.REACT_APP_METROHERO;
    

    const metroHeroMetrics = `https://dcmetrohero.com/api/v1/metrorail/metrics`;

    const requestHeaders: any = new Headers();
    requestHeaders.set('Content-Type', 'application/json');
    requestHeaders.set('Access-Control-Allow-Origin', '*');
    requestHeaders.set('Access-Control-Allow-Headers', 'Content-Type, Access-Control-Allow-Headers, X-Requested-With');
    requestHeaders.set('apiKey', apiKey);

    async function getHomeInfo() {
        let response = await fetch(metroHeroMetrics, {
            headers:  requestHeaders
        });

        let lineData = await response.json();

        console.log(lineData.lineMetricsByLine);

        let lineArray = []

        for(let line of lineData.lineMetricsByLine) {
            lineArray.push({
                lineCode : lines.lineCode,
                maximumTrainDelay : line.maximumTrainDelay,
                averageTrainFrequency : line.averageTrainFrequency,
                expectedTrainFrequency : line.expectedTrainFrequency,
                averagePlatformWaitTime : line.averagePlatformWaitTime,
                platformWaitTimeTrendStatus : line.platformWaitTimeTrendStatus,
                numTrains : line.numTrains,
                trainFrequencyStatus : line.trainFrequencyStatus

            })
        }

        console.log(lineArray)

        setMetroLineData(lineArray);
    }

    useEffect(() => {
        getHomeInfo();
    }, [])



    return (
        <p>Test</p>
    )
}

export default Home;
